  provider_name, provider_code, remark
  )
values (#{goodsId,jdbcType=INTEGER}, #{goodsName,jdbcType=VARCHAR}, #{goodsCode,jdbcType=VARCHAR}, 
  #{goodsVersion,jdbcType=INTEGER}, #{goodsShelfLife,jdbcType=INTEGER}, #{goodsUnit,jdbcType=VARCHAR}, 
  #{goodsBarCode,jdbcType=VARCHAR}, #{goodsPrice,jdbcType=BIGINT}, #{providerId,jdbcType=INTEGER}, 
  #{providerName,jdbcType=VARCHAR}, #{providerCode,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )
